# Giorgio Valdman - Drawing with Turtle

# Import other program in Python
from turtle import *

bgcolor("#20d64b")
color("black")
speed()
times=20

dot(50000000, "red")
while times>0:
    loop=12
    while loop>0:
        forward(100)
        right(30)
        loop= loop-1
    right(20)
    times= times-1
